function UserBarContent({ user, func }) {
  const [follower, setFollower] = useState();
  const [followerNum, setFollowerNum] = useState();
  const [followingNum, setFollowingNum] = useState();
  const { sessionUser } = SessionCurrent();
  const navigate = useNavigate();
  const [sessionUserFollow, setSessionUserFollow] = useState();
  const [followChange, setFollowChange] = useState(false);

  useEffect(() => {
    GetFollowing();
    GetFollower();
    GetFollowerNum();
  }, [user]);

  useEffect(() => {
    if (sessionUser && follower) {
      IsUserFollow();
    }
  }, [sessionUser, follower, user]);

  useEffect(() => {
    GetFollower();
    GetFollowerNum();
  }, [followChange]);

  async function GetFollower() {
    try {
      const response = await axios.post(
        "http://localhost:8080/api/userFollow/user2",
        { userId: user.userId }
      );
      const data = response.data;
      // console.log("GetFollower", data);
      setFollower(data);
    } catch (error) {
      console.log("요청에 실패했습니다.", error);
    }
  }

  async function GetFollowerNum() {
    try {
      const response = await axios.post(
        "http://localhost:8080/api/numberOfFollowByUser2",
        { userId: user.userId }
      );
      const data = response.data;
      // console.log("GetFollower", data);
      setFollowerNum(data);
    } catch (error) {
      console.log("요청에 실패했습니다.", error);
    }
  }

  async function GetFollowing() {
    try {
      const response = await axios.post(
        "http://localhost:8080/api/numberOfFollowByUser",
        { userId: user.userId }
      );
      const data = response.data;
      // console.log("GetFollowing", data);
      setFollowingNum(data);
    } catch (error) {
      console.log("요청에 실패했습니다.", error);
    }
  }

  function IsUserFollow() {
    setSessionUserFollow();
    follower.map((userFollow) => {
      if (userFollow.user.userId == sessionUser) {
        setSessionUserFollow(userFollow.id);
        return;
      }
    });
  }

  async function FollowClick() {
    if (sessionUser == "anonymousUser") {
      const result = window.confirm(
        "로그인이 필요한 서비스입니다. 로그인 창으로 이동할까요?"
      );
      if (result) {
        window.location.href = "/login";
      }
      return;
    }

    try {
      if (sessionUserFollow) {
        const response = await axios.delete(
          "http://localhost:8080/api/userFollow",
          {
            headers: {
              "Content-Type": "application/json",
            },
            data: {
              id: sessionUserFollow,
            },
          }
        );
        const data = response.data;
        console.log("FollowClick", data);
        setFollowChange(!followChange);
        func();
      } else {
        const response = await axios.post(
          "http://localhost:8080/api/userFollow",
          { user: { userId: sessionUser }, user2: { userId: user.userId } }
        );
        const data = response.data;
        console.log("FollowClick", data);
        setFollowChange(!followChange);
        func();
      }
    } catch (error) {
      console.log("요청에 실패했습니다.", error);
    }
  }

  return (
    <>
      <TooltipFlex>
        <div style={{ width: "30px", height: "30px" }}>
          <ProfileImage
            src={user.data ? getServerImgFile(user.data) : defaltUserImg}
            onClick={() => {
              navigate("/feed/user/" + user.userId);
            }}
          />
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            width: "100%",
          }}
        >
          <div>
            <div>{user.userId}</div>
            <div>
              팔로워 {followerNum} 팔로잉 {followingNum}
            </div>
          </div>
          {sessionUser != user.userId ? (
            sessionUserFollow ? (
              <FollowingBtn
                onClick={() => {
                  FollowClick();
                }}
              >
                팔로잉
              </FollowingBtn>
            ) : (
              <FollowerBtn
                onClick={() => {
                  FollowClick();
                }}
              >
                팔로우
              </FollowerBtn>
            )
          ) : null}
        </div>
      </TooltipFlex>
    </>
  );
}
